forcast=WeatherAPI.WeatherScripts.generalAPICall(value)['properties']['periods']
tempData=[]
humidityData=[]
for x in forcast:
	dateTime=system.date.parse(x['endTime'],"yyyy-MM-dd'T'HH:mm:ssXXX")
	if self.getChild("root").getChild("MultiStateButton").props.controlValue==1:
		time=system.date.toMillis(dateTime)
		tempData.append({"Temperature": x['temperature'], "Dewpoint":  x['dewpoint']['value']*1.8+32, "time": time})
		humidityData.append({"Humidity": x['relativeHumidity']['value'], 'Precip': x['probabilityOfPrecipitation']['value'], "time": time})
	elif system.date.isBefore(dateTime,system.date.addDays(system.date.now(),1)):
		time=system.date.toMillis(dateTime)
		tempData.append({"Temperature": x['temperature'], "Dewpoint":  x['dewpoint']['value']*1.8+32, "time": time})
		humidityData.append({"Humidity": x['relativeHumidity']['value'], 'Precip': x['probabilityOfPrecipitation']['value'], "time": time})
tempSeries={'name': 'Temp', 'data': tempData}
humSeries={'name': 'Humidity', 'data': humidityData}

return [tempSeries, humSeries]
